import {GetChannelText, GetVideoText } from "../connector/youtube.connector";

const _tabsConvertToGridRenderer = tabs => tabs && tabs.filter(
            tab => tab.tabRenderer && tab.tabRenderer.content
            )[0]
            .tabRenderer
            .content
            .sectionListRenderer
            .contents[0]
            .itemSectionRenderer
            .contents[0]
            .gridRenderer
            .items;

const GetVideo = async videoId => {
    const videoText = await GetVideoText(videoId);
    const preJson = videoText.split("window[\"ytInitialData\"] = ")[1].split(";")[0]
    const json = await JSON.parse(preJson);        
    const gridVideoRenderer = await json.contents
                    .twoColumnWatchNextResults
                    .results
                    .results
                    .contents[0]
                    .videoPrimaryInfoRenderer;
    console.log(gridVideoRenderer);
    return ""
    /*
    return {
            "videoId" : gridVideoRenderer.videoId,
            "thumbnail" : gridVideoRenderer.thumbnail.thumbnails,
            "title" : gridVideoRenderer.title.simpleText,
            "publishedTimeText" : gridVideoRenderer.publishedTimeText.simpleText,
            "viewCountText" : gridVideoRenderer.viewCountText.simpleText
      };
     */
}


const GetDataChannel = async channel => {
  const channelText = await GetChannelText(channel);
  const preJson = channelText.split("window[\"ytInitialData\"] = ")[1].split(";")[0]
  const json = JSON.parse(preJson);        
  const tabs = json.contents.twoColumnBrowseResultsRenderer.tabs;

  const items = await _tabsConvertToGridRenderer(tabs);
  return await items.map(async ({ gridVideoRenderer }) => gridVideoRenderer.videoId)  


}
export { GetDataChannel, GetVideo };
